    private void comboVendorNameAutoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboVendorNameAutoActionPerformed
//        //        DefaultComboBoxModel dfComboModelVendor = (DefaultComboBoxModel) this.comboVendorNameAuto.getModel();
        Vendors vendorBean = (Vendors) this.comboVendorNameAuto.getSelectedItem();
        currentBalance.setText("" + vendorBean.getCurrentBalance());
    }//GEN-LAST:event_comboVendorNameAutoActionPerformed

    private void comboPaymentMethodActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboPaymentMethodActionPerformed
//        DefaultComboBoxModel dfComboModel = (DefaultComboBoxModel) this.comboPaymentMethod.getModel();
        String item = comboPaymentMethod.getSelectedItem().toString();
        if (item.equals("Counter Cash")) {
            jLabel5.setVisible(false);
            txtCheque.setVisible(false);
            txtCheque.setText("");
        } else {
            jLabel5.setVisible(true);
            txtCheque.setVisible(true);
        }
    }//GEN-LAST:event_comboPaymentMethodActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Global.addPanel(new PayablePanel(title), (JPanel) this.getParent());
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try {
            PayableReceiveable tran = new PayableReceiveable();
            tran.setDate(jXDatePicker1.getDate().getTime());
            tran.setAccountId(((Account) comboPaymentMethod.getSelectedItem()).getId());
            tran.setPartyId(((Vendors) comboVendorNameAuto.getSelectedItem()).getId());
            tran.setAmount(Double.parseDouble(etAmount.getText()));
            tran.setChequeNo(txtCheque.getText());
            tran.setParty((Vendors) comboVendorNameAuto.getSelectedItem());
            tran.setPartyType(title);
            tran.setRemarks(etRemarks.getText());

            Response insertRecord = Global.CONTROLLER.addPayableReceiveable(tran);
            if (insertRecord.isError()) {
                JOptionPane.showMessageDialog(this, insertRecord.getErrorMsg());
            } else {
                Global.addPanel(new PayablePanel(title), (JPanel) this.getParent());
            }
        } catch (Exception e) {
            e.printStackTrace();
        }


    }//GEN-LAST:event_jButton2ActionPerformed


