    private void loadData(String title) {
        jXDatePicker1.setDate(new Date());
        ArrayList vendorsList = new ArrayList();
        ArrayList accountsList = DBM.getAllRecords(Account.class);
        switch (title) {
            case "Payable":
                System.out.println("asd");
                vendorsList = DBM.getRecordsBy(Vendors.class, "Type", "Vendor");
                System.out.println(vendorsList.size());
                break;
            case "Receiveable":
                System.out.println("askhf");
                vendorsList = DBM.getRecordsBy(Vendors.class, "Type", "Customer");
                jLabel3.setText("Customer Name");
                jLabel4.setText("Customer Balance");
                break;
        }
        Global.addListToCombo(comboVendorNameAuto, vendorsList);
        Global.addListToCombo(comboPaymentMethod, accountsList);
        AutoCompleteDecorator.decorate(comboVendorNameAuto);
    }
